import React, { useState } from "react";
import { Box, Button, Typography } from "@mui/material";

function App() {
  const [step, setStep] = useState(0);
  const [roseRain, setRoseRain] = useState(false);
  const [textRain, setTextRain] = useState(false);

  const handleCatch = () => {
    setRoseRain(true);
    setTimeout(() => {
      setStep(1);
    }, 3000);
  };

  const handleLove = () => {
    setTextRain(true);
    setTimeout(() => {
      setStep(3);
    }, 3000);
  };

  return (
    <Box
      sx={{
        height: "100vh",
        background: "linear-gradient(to right, #ff9a9e, #fad0c4)",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        flexDirection: "column",
        overflow: "hidden",
        position: "relative",
      }}
    >
      {/* Rose Rain */}
      {roseRain &&
        Array.from({ length: 30 }).map((_, i) => (
          <Typography
            key={i}
            sx={{
              position: "absolute",
              top: "-10%",
              left: `${Math.random() * 100}%`,
              fontSize: "30px",
              animation: `fall 3s linear infinite`,
            }}
          >
            üåπ
          </Typography>
        ))}

      {/* Text Rain */}
      {textRain &&
        Array.from({ length: 40 }).map((_, i) => (
          <Typography
            key={i}
            sx={{
              position: "absolute",
              top: "-10%",
              left: `${Math.random() * 100}%`,
              fontSize: "18px",
              color: "white",
              animation: `fall 3s linear infinite`,
            }}
          >
            I love you too ‚ù§Ô∏è
          </Typography>
        ))}

      {/* Step 0 */}
      {step === 0 && (
        <Button
          variant="contained"
          color="error"
          sx={{
            borderRadius: "50px",
            padding: "15px 40px",
            fontSize: "20px",
            animation: "float 2s ease-in-out infinite",
          }}
          onClick={handleCatch}
        >
          Catch Me If You Can üíò
        </Button>
      )}

      {/* Step 1 */}
      {step === 1 && (
        <Button
          variant="contained"
          color="secondary"
          sx={{
            borderRadius: "50px",
            padding: "15px 40px",
            fontSize: "20px",
          }}
          onClick={handleLove}
        >
          Do You Love Me? üíå
        </Button>
      )}

      {/* Final Page */}
      {step === 3 && (
        <Box textAlign="center">
          <Typography variant="h3" color="white">
            Happy Valentine's Day ‚ù§Ô∏è
          </Typography>
          <Typography variant="h5" mt={2} color="white">
            You are the most beautiful part of my life üíï
          </Typography>
        </Box>
      )}

      <style>
        {`
          @keyframes fall {
            0% { transform: translateY(0); opacity: 1; }
            100% { transform: translateY(110vh); opacity: 0; }
          }

          @keyframes float {
            0% { transform: translateY(0px); }
            50% { transform: translateY(-10px); }
            100% { transform: translateY(0px); }
          }
        `}
      </style>
    </Box>
  );
}

export default App;
