import React, { useState, useRef } from "react";
import { Box, Button, Typography, TextField } from "@mui/material";
import { motion } from "framer-motion";

function App() {
  const [step, setStep] = useState(0);
  const [name, setName] = useState("");
  const [roseRain, setRoseRain] = useState(false);
  const [textRain, setTextRain] = useState(false);
  const audioRef = useRef(null);

  const startMusic = () => {
    audioRef.current.play();
  };

  const handleCatch = () => {
    startMusic();
    setRoseRain(true);
    setTimeout(() => {
      setStep(1);
    }, 3000);
  };

  const handleLove = () => {
    setTextRain(true);
    setTimeout(() => {
      setStep(3);
    }, 3000);
  };

  return (
    <Box
      sx={{
        height: "100vh",
        width: "100%",
        background: "linear-gradient(135deg, #ff758c, #ff7eb3)",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        flexDirection: "column",
        overflow: "hidden",
        position: "relative",
        textAlign: "center",
        px: 2,
      }}
    >
      {/* Background Music */}
      <audio
        ref={audioRef}
        loop
        src="https://www.bensound.com/bensound-music/bensound-romantic.mp3"
      />

      {/* Rose Rain */}
      {roseRain &&
        Array.from({ length: 25 }).map((_, i) => (
          <motion.div
            key={i}
            initial={{ y: -100, x: Math.random() * window.innerWidth }}
            animate={{ y: "110vh" }}
            transition={{
              duration: 3,
              repeat: Infinity,
              delay: Math.random() * 2,
            }}
            style={{
              position: "absolute",
              fontSize: "28px",
            }}
          >
            üåπ
          </motion.div>
        ))}

      {/* Text Rain */}
      {textRain &&
        Array.from({ length: 30 }).map((_, i) => (
          <motion.div
            key={i}
            initial={{ y: -100, x: Math.random() * window.innerWidth }}
            animate={{ y: "110vh" }}
            transition={{
              duration: 3,
              repeat: Infinity,
              delay: Math.random() * 2,
            }}
            style={{
              position: "absolute",
              color: "white",
              fontWeight: "bold",
              fontSize: "16px",
            }}
          >
            I love you too ‚ù§Ô∏è
          </motion.div>
        ))}

      {/* STEP 0 ‚Äì Name Input */}
      {step === 0 && (
        <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }}>
          <Typography variant="h4" color="white" mb={2}>
            Enter Her Beautiful Name üíï
          </Typography>

          <TextField
            variant="outlined"
            value={name}
            onChange={(e) => setName(e.target.value)}
            sx={{
              backgroundColor: "white",
              borderRadius: 2,
              mb: 3,
              width: { xs: "100%", sm: "300px" },
            }}
          />

          <br />

          <motion.div
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
          >
            <Button
              variant="contained"
              color="error"
              sx={{ borderRadius: "30px", px: 4 }}
              onClick={handleCatch}
              disabled={!name}
            >
              Catch Me If You Can üíò
            </Button>
          </motion.div>
        </motion.div>
      )}

      {/* STEP 1 */}
      {step === 1 && (
        <motion.div
          initial={{ scale: 0 }}
          animate={{ scale: 1 }}
        >
          <Typography variant="h5" color="white" mb={3}>
            {name}, Do You Love Me? üíå
          </Typography>

          <motion.div
            whileHover={{ scale: 1.2 }}
            whileTap={{ scale: 0.9 }}
          >
            <Button
              variant="contained"
              color="secondary"
              sx={{ borderRadius: "30px", px: 4 }}
              onClick={handleLove}
            >
              YES üíñ
            </Button>
          </motion.div>
        </motion.div>
      )}

      {/* FINAL PAGE */}
      {step === 3 && (
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
        >
          <Typography variant="h3" color="white" mb={2}>
            Happy Valentine‚Äôs Day üíï
          </Typography>

          <Typography variant="h5" color="white">
            Dear {name},  
          </Typography>

          <Typography variant="body1" mt={2} color="white">
            You are the most precious gift in my life.  
            Every moment with you feels magical.  
            I love you more than words can describe. ‚ù§Ô∏è
          </Typography>
        </motion.div>
      )}
    </Box>
  );
}

export default App;
