import React, { useState, useRef } from "react";
import { Box, Button, Typography } from "@mui/material";
import { motion } from "framer-motion";

function App() {
  const [step, setStep] = useState(0);
  const [roseRain, setRoseRain] = useState(false);
  const [textRain, setTextRain] = useState(false);
  const audioRef = useRef(null);

  const name = "Israt Jahan Munni Nur";

  const startMusic = () => {
    audioRef.current.play();
  };

  const handleCatch = () => {
    startMusic();
    setRoseRain(true);
    setTimeout(() => setStep(1), 3000);
  };

  const handleLove = () => {
    setTextRain(true);
    setTimeout(() => setStep(2), 3500);
  };

  return (
    <Box
      sx={{
        height: "100vh",
        width: "100%",
        background: "radial-gradient(circle at top, #ff4e8b, #8e2de2)",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        flexDirection: "column",
        overflow: "hidden",
        position: "relative",
        textAlign: "center",
        px: 2,
      }}
    >
      {/* Romantic Music */}
      <audio
        ref={audioRef}
        loop
        src="https://www.bensound.com/bensound-music/bensound-romantic.mp3"
      />

      {/* Rose Rain */}
      {roseRain &&
        Array.from({ length: 25 }).map((_, i) => (
          <motion.div
            key={i}
            initial={{ y: -100, x: Math.random() * window.innerWidth }}
            animate={{ y: "110vh" }}
            transition={{
              duration: 3,
              repeat: Infinity,
              delay: Math.random() * 2,
            }}
            style={{ position: "absolute", fontSize: "28px" }}
          >
            üåπ
          </motion.div>
        ))}

      {/* Text Rain */}
      {textRain &&
        Array.from({ length: 35 }).map((_, i) => (
          <motion.div
            key={i}
            initial={{ y: -100, x: Math.random() * window.innerWidth }}
            animate={{ y: "110vh" }}
            transition={{
              duration: 3,
              repeat: Infinity,
              delay: Math.random() * 2,
            }}
            style={{
              position: "absolute",
              color: "white",
              fontWeight: "bold",
              fontSize: "16px",
            }}
          >
            I love you too ‚ù§Ô∏è
          </motion.div>
        ))}

      {/* STEP 0 */}
      {step === 0 && (
        <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }}>
          <Typography variant="h4" color="white" mb={3}>
            Hello {name} üíï
          </Typography>

          <motion.div whileHover={{ scale: 1.15 }} whileTap={{ scale: 0.9 }}>
            <Button
              variant="contained"
              color="error"
              sx={{ borderRadius: "30px", px: 4 }}
              onClick={handleCatch}
            >
              Catch Me If You Can üíò
            </Button>
          </motion.div>
        </motion.div>
      )}

      {/* STEP 1 */}
      {step === 1 && (
        <motion.div initial={{ scale: 0 }} animate={{ scale: 1 }}>
          <Typography variant="h5" color="white" mb={3}>
            {name}, Do You Love Me? üíå
          </Typography>

          <motion.div whileHover={{ scale: 1.2 }} whileTap={{ scale: 0.9 }}>
            <Button
              variant="contained"
              color="secondary"
              sx={{ borderRadius: "30px", px: 4 }}
              onClick={handleLove}
            >
              YES üíñ
            </Button>
          </motion.div>
        </motion.div>
      )}

      {/* FINAL STEP */}
      {step === 2 && (
        <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }}>
          <Typography
            variant="h3"
            color="white"
            mb={2}
            sx={{ fontWeight: "bold" }}
          >
            Happy Valentine‚Äôs Day üíï
          </Typography>

          <Typography variant="h5" color="white">
            My Dearest {name},
          </Typography>

          <Typography
            variant="body1"
            mt={3}
            color="white"
            sx={{ maxWidth: "600px", mx: "auto" }}
          >
            From the moment you came into my life, everything became more
            beautiful. Your smile brightens my darkest days, your voice
            calms my storms, and your presence makes my world complete.
            <br /><br />
            I am truly blessed to have you.  
            Today and every day ‚Äî I choose you. ‚ù§Ô∏è
          </Typography>
        </motion.div>
      )}
    </Box>
  );
}

export default App;
